import React, { useEffect, useState } from "react";
import { createAPIEndpoint, ENDPOINTS } from "../api";
import useStateContext from "../hooks/useStateContext";
import { Card, CardContent, Typography, List, ListItemButton} from "@mui/material";


export default function Quiz() {
  const [qns, setQns] = useState([]);
  const [qnsIndex, setQnsIndex] = useState(0);
  // setContext({ timeTaken: 1 });

  useEffect(() => {
    createAPIEndpoint(ENDPOINTS.question)
      .fetch()
      .then((res) => {
        setQns(res.data);
        console.log(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);
  return (
    qns.length != 0
    ? <Card>
       <CardContent>
        <Typography variant ="h6">
          {qns[qnsIndex].qnInWords}
        </Typography>
        <List>
            {qns[qnsIndex].options.map((item, idx) =>            
            <ListItemButton key={idx}  disableRiple>

            </ListItemButton>            
            )}
          </List>
       </CardContent>
      </Card> 
      : null
  )
}
