import React, { useEffect, useState } from "react";
import { createAPIEndpoint, ENDPOINTS } from "../api";
import useStateContext from "../hooks/useStateContext";
import { Card, CardContent, CardHeader, Typography, List, ListItemButton} from "@mui/material";


export default function Quiz() {
  const [qns, setQns] = useState([]);
  const [qnsIndex, setQnsIndex] = useState(0);

  useEffect(() => {
    createAPIEndpoint(ENDPOINTS.question)
      .fetch()
      .then((res) => {
        setQns(res.data);
        console.log(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);
  return (
    qns.length != 0
    ? <Card sx={{maxWidth:640, mx: 'auto, mt: 5'}}>
      <CardHeader title={'Question ' + (qnsIndex + 1) + ' of 5'}>
      
      </CardHeader>
       <CardContent>
        <Typography variant ="h6">
          {qns[qnsIndex].qnInWords}
        </Typography>
        <List>
            {qns[qnsIndex].options.map((item, idx) =>            
            <ListItemButton key={idx}  disableRipple>
              <div>
               <b> {String.fromCharCode(65 + idx)+ " . "} </b> {item}
              </div>

            </ListItemButton>            
            )}
          </List>
       </CardContent>
      </Card> 
      : null
  )
}
